mapper.select:
select TASK_NAME taskName, TASK_CLASS taskClass, CRON_EXPRESS cronExpress, LAST_EXECUTED lastExecuted, NEXT_EXECUTE nextExecute, REMARK remark, PERIOD_VALUE periodValue, PERIOD_UNIT periodUnit from test.task_watermark
AND TASK_CLASS = #{TASK_CLASS}
NEXT_EXECUTE < now()

mapper.update:
UPDATE test.task_watermark
SET LAST_EXECUTED = #{LAST_EXECUTED},
	NEXT_EXECUTE = #{NEXT_EXECUTE}
WHERE  TASK_CLASS = #{TASK_CLASS}

UPDATE test.task_watermark
SET LAST_EXECUTED = #{NEXT_EXECUTE}
	NEXT_EXECUTE = date_add(#{NEXT_EXECUTE}, interval ${PERIOD_VALUE} ${PERIOD_UNIT})
WHERE  TASK_CLASS = #{TASK_CLASS}
